import React from "react";
import MealTypeStatic from "./data/MealTypeStatic";

const Recipe = () => {
  const recipeObj = MealTypeStatic[0];
  const stepsObj = recipeObj.analyzedInstructions[0].steps;

  const printSteps = stepsObj.map((eachStep) => {
    return <li>{eachStep.step}</li>;
  });
  return (
    <>
      <div className="container recipe">
        <div className="row recipeUpper">
          <div className="col-5">
            <img src={recipeObj.image}></img>
          </div>
          <div className="col-7">
            <h2 id="recipeTitle">{recipeObj.title}</h2>
            <div id="timeServing">
              <div>
                <h4>Prep time</h4>
                <i className="fa fa-cut-o"></i>
                <p>{recipeObj.preparationMinutes}</p>
              </div>
              <div>
                <h4>Total time</h4>
                <p>{recipeObj.readyInMinutes}</p>
              </div>
              <div>
                <h4>Servings</h4>
                <p>{recipeObj.servings}</p>
              </div>
            </div>
          </div>
        </div>
        <div className="row">
          <ol>Steps</ol>
          {printSteps}
        </div>
      </div>
    </>
  );
};

export default Recipe;
