import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { inputSearchAction } from "../store/inputSearch";
import { inputSuggestionsAction } from "../store/inputSuggestions";

const NavBar = () => {
  const dispatch = useDispatch();
  const inputSearch = useSelector((state) => state.inputSearch.input);
  const inputSuggestions = useSelector(
    (state) => state.inputSuggestions.suggestions
  );
  const [input, setInput] = useState("");

  const updateInput = () => {
    dispatch(inputSearchAction.setInputSearch(input));
  };

  const handleInputChange = (event) => {
    setInput(event.target.value);
  };

  return (
    <nav>
      <a>Home</a>
      <a>Meal type</a>
      <a>Shopping List</a>
      <a>Favourites</a>
      {input}
      <div id="inputDiv">
        <input
          placeholder="Search Recipes"
          type="text"
          value={input}
          onChange={handleInputChange}
        ></input>
        <i className="fa fa-search" onClick={updateInput}></i>
      </div>
    </nav>
  );
};

export default NavBar;
