import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { inputSearchAction } from "../store/inputSearch";
import { inputSuggestionsAction } from "../store/inputSuggestions";

const NavBar = () => {
  const dispatch = useDispatch();
  const inputSearch = useSelector((state) => state.inputSearch.input);
  const inputSuggestions = useSelector(
    (state) => state.inputSuggestions.suggestions
  );

  const handleInputChange = (event) => {
    dispatch(inputSearchAction.setInputSearch(event.target.value));
  };

  return (
    <nav>
      <a>Home</a>
      <a>Meal type</a>
      <a>Shopping List</a>
      <a>Favourites</a>
      {inputSearch}
      <div id="inputDiv">
        <input
          placeholder="Search Recipes"
          type="text"
          // value={inputSearch}
          onChange={handleInputChange}
        ></input>
        <i className="fa fa-search"></i>
      </div>
    </nav>
  );
};

export default NavBar;
