import React from "react";

const Card = (props) => {
  const cuisineArr = props.cuisines;
  const cuisineStr = "";
  for (let i = 0; i < 3; i++) {
    cuisineStr += cuisineArr[0];
    if (i < 2) {
      cuisineStr += ", ";
    }
  }
  return (
    <div className="eachImage col">
      <img src={props.image}></img>
      <p className="cuisines">{cuisineStr}</p>
      <p className="title">{props.title}</p>
    </div>
  );
};

export default Card;
